  #{codemetasrcfld,jdbcType=NVARCHAR}, #{codemetasrcitem,jdbcType=NVARCHAR}, #{codemetavaluefld,jdbcType=NVARCHAR},
  #{codemetavaluefldname,jdbcType=NVARCHAR}, #{codemetashowfld,jdbcType=NVARCHAR}, #{codemetashowfldname,jdbcType=NVARCHAR},
  #{codemetasortfld,jdbcType=NVARCHAR}, #{codemetasortfldname,jdbcType=NVARCHAR}, 
  #{codemetatype,jdbcType=NVARCHAR}, #{showornot,jdbcType=CHAR}, #{codemetasort,jdbcType=INTEGER}
  )

mapper.select:
