mapper.select:
select 

from DataFile
where dfDataId = #{dfdataid,jdbcType=BIGINT}
  and dfFileSeq = #{dffileseq,jdbcType=NVARCHAR}

mapper.update:
update DataFile
set dfFileSort = #{dffilesort,jdbcType=INTEGER},
  dfFilePublic = #{dffilepublic,jdbcType=CHAR}
where dfDataId = #{dfdataid,jdbcType=BIGINT}
  and dfFileSeq = #{dffileseq,jdbcType=NVARCHAR}

mapper.select:
select a.dfDataId, a.dfFileSeq, a.dfFileSort, a.dfFilePublic, b.fileid, b.fileFileUri, b.fileFileName from DataFile a left join FileCenter b on a.dfFileSeq = b.fileSeq where dfDataId = #{dfdataid,jdbcType=BIGINT}
and dfFilePublic = #{ispublic}
order by a.dfFileSort
