mapper.select:
   select a.ugrpId, a.apId, a.apFunc, b.apCName 
   from BaphiqUgrpAp a 
	left join BaphiqAp b 
	on a.apId=b.apId 
   where a.ugrpId = #{ugrpid,jdbcType=INTEGER}
     and a.apId = #{apid,jdbcType=INTEGER}

mapper.delete:
delete from BaphiqUgrpAp
where ugrpId = #{ugrpid,jdbcType=INTEGER}
  and apId = #{apid,jdbcType=INTEGER}

mapper.update:
update BaphiqUgrpAp
set apFunc = #{apfunc,jdbcType=INTEGER} 
where ugrpId = #{ugrpid,jdbcType=INTEGER}
  and apId = #{apid,jdbcType=INTEGER}
